const initialState = {
    movies: [],

  };

const applyMoviesChange = (state = initialState, action) => {
    switch (action.type) {
      case 'LOAD':
        return { ...state, movies: action.payload };

      default:
        return state;
    }
  };

export  {applyMoviesChange};
