import axios from 'axios'
import { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { doEditUser } from '../redux/UsersActions';

import Grid from "@mui/material/Grid";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import TextField from "@mui/material/TextField";

import { styled } from '@mui/material/styles';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';

import CardActions from '@mui/material/CardActions';
import Collapse from '@mui/material/Collapse';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';

import { red } from '@mui/material/colors';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import MoreVertIcon from '@mui/icons-material/MoreVert';

const usersUrl = 'http://localhost:8000/users';

const UsersManagement = ({username}) => {
    const dispatch = useDispatch()
    const navigate = useNavigate();
    const users = useSelector(state => state.UsersReducer.users);
    const user = users.find((user)=> user.username === username.toLowerCase())
    const [editUser, setEditUser] = useState(user)
    const [editUserErrors, setEditUserErrors] = useState({})
    const [isSubmit, setIsSubmit] = useState(false)
    const [expanded, setExpanded] = useState(false);
    console.log(user)

    const handleSubmit = async (e) => {
      e.preventDefault();
      setIsSubmit(true);
      setEditUserErrors(validate(editUser));
    };

    useEffect(() => {
      (async () => {if(Object.keys(setEditUserErrors).length === 0 && isSubmit){
        const { data } = await axios.put(`${usersUrl}/${user._id}`, editUser);
        console.log(data)
        setEditUser(editUser);
        dispatch(doEditUser(editUser));
        navigate('/mainpage')
      }})()
    }, [editUserErrors])

    const handleExpandClick = () => {
        setExpanded(!expanded);
      };



    const cancel = () => {
      navigate('/mainpage')
  }

    const validate = (values) => {
      const errors = {}
      if(!values.fullName) {
          errors.fullName = "Full Name is required!";
      }
      if(!values.username) {
          errors.username = "Username is required!";
      }
      if(!values.password) {
          errors.password = "Password is required!";
      }else if(values.password.length < 5){
          errors.password = "Password must be more than 5 characters!"
      }
      return errors;
    }

    const ExpandMore = styled((props) => {
        const { expand, ...other } = props;
        return <IconButton {...other} />;
      })(({ theme, expand }) => ({
        transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
          duration: theme.transitions.duration.shortest,
        }),
      }));



  return (
    <div style={{marginTop: '20px', marginLeft: '10px'}}>
    <Card sx={{ width: '50%' }}>
    <form onSubmit={handleSubmit}>
    <CardContent>
      <Typography sx={{ fontSize: 17 }} color="text.secondary" gutterBottom>
          Edit User: {user.fullName} 
      </Typography>   
      <Grid container direction={"column"} spacing={3}>
      <Grid item>
                <TextField
                  label="Full Name:"
                  value={editUser.fullName}
                  multiline
                  name="fullName"
                  onChange={(e) =>
                    setEditUser({ ...editUser, fullName: e.target.value })
                  }
                />
              </Grid>
              <Grid item style={{color: 'red'}}>
                  {editUserErrors.fullName}
              </Grid>
              <Grid item>
                <TextField
                  label="User Name:"
                  value={editUser.username}
                  multiline
                  name="username"
                  onChange={(e) =>
                      setEditUser({ ...editUser, username: e.target.value })
                  }
                  />
              </Grid>
              <Grid item style={{color: 'red'}}>
                  {editUserErrors.username}
              </Grid>
              <Grid item>
                <TextField
                  id="outlined-password-input"
                  label="Password"
                  type="password"
                  value={editUser.password}
                  autoComplete="current-password"
                  name="password"
                  onChange={(e) =>
                  setEditUser({ ...editUser, password: e.target.value })
                  }
                  />
              </Grid>
              <Grid item style={{color: 'red'}}>
                  {editUserErrors.password}
              </Grid>
              <Grid item>
                <Button type="submit" variant="contained">
                  Update
                </Button>{" "}
                <Button variant="contained" onClick={cancel}>Cancel</Button> {' '}
              </Grid>
              </Grid>
    </CardContent>
    </form>
  </Card>

  <Card sx={{ maxWidth: 345 }}>
      <CardHeader
        avatar={
          <Avatar sx={{ bgcolor: red[500] }} aria-label="recipe">
            R
          </Avatar>
        }
        action={
          <IconButton aria-label="settings">
            <MoreVertIcon />
          </IconButton>
        }
        title="Shrimp and Chorizo Paella"
        subheader="September 14, 2016"
      />
      <CardMedia
        component="img"
        height="194"
        image="/static/images/cards/paella.jpg"
        alt="Paella dish"
      />
      <CardContent>
        <Typography variant="body2" color="text.secondary">
          This impressive paella is a perfect party dish and a fun meal to cook
          together with your guests. Add 1 cup of frozen peas along with the mussels,
          if you like.
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon />
        </IconButton>
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
        <ExpandMore
          expand={expanded}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          <ExpandMoreIcon />
        </ExpandMore>
      </CardActions>
      <Collapse  timeout="auto" unmountOnExit>
        <CardContent>
          <Typography paragraph>Method:</Typography>
          <Typography paragraph>
            Heat 1/2 cup of the broth in a pot until simmering, add saffron and set
            aside for 10 minutes.
          </Typography>
          <Typography paragraph>
            Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over
            medium-high heat. Add chicken, shrimp and chorizo, and cook, stirring
            occasionally until lightly browned, 6 to 8 minutes. Transfer shrimp to a
            large plate and set aside, leaving chicken and chorizo in the pan. Add
            pimentón, bay leaves, garlic, tomatoes, onion, salt and pepper, and cook,
            stirring often until thickened and fragrant, about 10 minutes. Add
            saffron broth and remaining 4 1/2 cups chicken broth; bring to a boil.
          </Typography>
          <Typography paragraph>
            Add rice and stir very gently to distribute. Top with artichokes and
            peppers, and cook without stirring, until most of the liquid is absorbed,
            15 to 18 minutes. Reduce heat to medium-low, add reserved shrimp and
            mussels, tucking them down into the rice, and cook again without
            stirring, until mussels have opened and rice is just tender, 5 to 7
            minutes more. (Discard any mussels that don&apos;t open.)
          </Typography>
          <Typography>
            Set aside off of the heat to let rest for 10 minutes, and then serve.
          </Typography>
        </CardContent>
      </Collapse>
    </Card>
  </div>
  )
}

export default UsersManagement
