import axios from 'axios'
import { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { doLoadMembers } from '../redux/MoviesActions';

const url = 'http://localhost:8000/members';

const MemberWatchComp = ({id}) => {

  const dispatch = useDispatch()
  const [members, setMembers] = useState([])

  useEffect(() => {
    const fetchMembers = async () => {
      const { data: membersData } = await axios.get(url);
      setMembers(membersData);
      console.log(members);
      dispatch(doLoadMembers(members));
    }
    fetchMembers();
  }, [])

  return (
    <div>{members.map((mem)=> mem._id === id ? mem.name : null )}</div>
  )
}

export default MemberWatchComp
