import axios from 'axios'
import { useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';

import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

const moviesUrl = 'http://localhost:8000/movies';

const EditMovieComp = () => {
    const { movieid } = useParams();
    const movies = useSelector(state => state.MoviesReducer.movies);
    const movie = movies.find((movie)=> movie._id === movieid)
    const [movieEdit, setMovieEdit] = useState(movie)

    const update = async () => {
        console.log(movieEdit)
        const resp = await axios.put(`${moviesUrl}/${movie._id}`, movieEdit);
        console.log(resp.data);
    }

  return (
    <div>

<Card sx={{ width: '50%' }}>
      <CardContent>
        <Typography sx={{ fontSize: 17 }} color="text.secondary" gutterBottom>
            Edit Movie: {movie.name} 
        </Typography>   
        <Typography variant="h6" component="div">
        Name: <input type="text"  name="name" onChange={(e) => setMovieEdit({ ...movieEdit, name: e.target.value })}/> <br />
        Genres: <input type="text" value={movie.genres} name="genres" onChange={(e) => setMovieEdit({ ...movieEdit, genres: e.target.value })}/> <br />
        Image URL: <input type="text" value={movie.image} name="image" onChange={(e) => setMovieEdit({ ...movieEdit, image: e.target.value })}/> <br />
        Premired: <input type="text" value={movie.yearPremiered} name="premiered" onChange={(e) => setMovieEdit({ ...movieEdit, premiered: e.target.value })}/> <br />
        </Typography>
      </CardContent>
      <CardActions>
        <Button variant="contained" onClick={update}>Update</Button> {' '}
        <Button variant="contained">Cancel</Button> {' '}
      </CardActions>
    </Card>
    </div>
  )
}

export default EditMovieComp
