import { combineReducers } from "redux";

const initialUsersState = {
    users: []

  };

  const initialMoviesState = {
    movies: []

  };

  const applyUsersChange = (state = initialUsersState, action) => {
    switch (action.type) {
      case 'LOAD':
        return { ...state, users: action.payload };

      default:
        return state;
    }
  };

  const applyMoviesChange = (state = initialMoviesState, action) => {
    switch (action.type) {
      case 'LOAD':
        return { ...state, movies: action.payload };

      default:
        return state;
    }
  };


export default combineReducers({applyMoviesChange,applyUsersChange})
