import axios from 'axios'
import { useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';

import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

const moviesUrl = 'http://localhost:8000/movies';

const EditMovieComp = () => {
    const [movieEdit, setMovieEdit] = useState({})
    const { movieid } = useParams();
    const movies = useSelector(state => state.MoviesReducer.movies);

    const movie = movies.find((movie)=> movie._id === movieid)

    const handleChange = (e) => {
        const { name, value } = e.target;
        setMovieEdit({...movieEdit, [name]: value})
    }

    const update = async () => {
        const resp = await axios.put(`${moviesUrl}/${movie.id}`, movieEdit);
        console.log(resp.data);
    }

  return (
    <div>

<Card sx={{ width: '50%' }}>
      <CardContent>
        <Typography sx={{ fontSize: 20 }} color="text.secondary" gutterBottom>
            Edit Movie: {movie.name} <br />
        </Typography>
        <Typography variant="h6" component="div">
        Name: <input type="text" value={movie.name} name="name" onChange={handleChange}/> <br />
        Genres: <input type="text" value={movie.genres} name="genres" onChange={handleChange}/> <br />
        Image URL: <input type="text" value={movie.image} name="image" onChange={handleChange}/> <br />
        Premired: <input type="text" value={movie.yearPremiered} name="premiered" onChange={handleChange}/> <br />
        </Typography>
      </CardContent>
      <CardActions>
        <Button variant="contained" onClick={update}>Update</Button> {' '}
        <Button variant="contained">Cancel</Button> {' '}
      </CardActions>
    </Card>
    </div>
  )
}

export default EditMovieComp
