import axios from 'axios'
import { useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { doEditMovie } from '../redux/MoviesActions';

import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';

const moviesUrl = 'http://localhost:8000/movies';

const AddMovieComp = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const [movieToAdd, setMovieToAdd] = useState({})

    const save = async () => {
        const { data } = await axios.post(`${moviesUrl}`, movieToAdd);
        dispatch(doEditMovie(movieToAdd));
        console.log(data);
        console.log(movieToAdd);
        // navigate(`/${username}/MainPage`)
    }

  return (
    <div>
        <Card sx={{ width: '75%' }}>
      <CardContent>
        <Typography variant="h7" component="div">
        Name:        <input type="text"   name="name" onChange={(e) => setMovieToAdd({ ...movieToAdd, name: e.target.value })}/> <br />
        Genres:      <input type="text"  name="genres" onChange={(e) => setMovieToAdd({ ...movieToAdd, genres: e.target.value.split(',') })}/> <br />
        Image URL:   <input type="text"  name="image" onChange={(e) => setMovieToAdd({ ...movieToAdd, image: e.target.value })}/> <br />
        Premired:    <input type="text"  name="premiered" onChange={(e) => setMovieToAdd({ ...movieToAdd, premiered: e.target.value })}/> <br />
        <TextField id="outlined-basic" label="Outlined" variant="outlined" size=''/>
        <TextField
          id="outlined-textarea"
          label="Multiline Placeholder"
          placeholder="Placeholder"
          multiline
        />
                <TextField
          id="outlined-textarea"
          label="Multiline Placeholder"
          placeholder="Placeholder"
          multiline
        />
        </Typography>
      </CardContent>
      <CardActions>
        <Button variant="contained" onClick={save}>Save</Button> {' '}
        {/* <Button variant="contained" onClick={cancel}>Cancel</Button> {' '} */}
      </CardActions>
    </Card>
    </div>
  )
}

export default AddMovieComp
