import { useState } from "react";
// import Button from '@material-ui/core/Button'
// import ButtonGroup from '@material-ui/core/ButtonGroup'
import { ToggleButtonGroup, ToggleButton } from "@mui/material";
import MoviesComp from '../components/MoviesComp';

const MainPage = () => {
  const [currentAlignment, setCurrentAlignment] = useState("movies");
  const [comp, setComp] = useState('')


  return (
    <div>
      <ToggleButtonGroup
        value={currentAlignment}
        onChange={(event, newAlignment) => {
          setCurrentAlignment(newAlignment);

         if(comp==='movies'){
             console.log(comp)
             return <MoviesComp/>
         }
        }} 
        exclusive
        variant="contained"
        aria-label="text formatting"
        color="primary"

      >
        <ToggleButton value="movies" onClick={(e)=>{setComp(e.target.value)}}>Movies</ToggleButton>
        <ToggleButton value="subscriptions">Subscriptions</ToggleButton>
        <ToggleButton value="usersManagement">Users Management</ToggleButton>
        <ToggleButton value="logout">Logout</ToggleButton>
        <MoviesComp/>
      </ToggleButtonGroup>


    </div>
  );
};

export default MainPage;
