import { useState } from 'react'
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';

import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';

const LoginComp = () => {
  const users = useSelector(state => state.UsersReducer.users);

  const [userLogin, setUserLogin] = useState({ username: '', password: ''})
  const navigate = useNavigate();

  const checkUser = () => {
    const usercheck = users.find(user => (user.username === userLogin.username && user.password === userLogin.password));
    if(usercheck) {
      sessionStorage.setItem('key', (userLogin.username.charAt(0).toUpperCase()) + userLogin.username.slice(1));
      navigate('/mainpage')
      console.log("Login successful");
    }else {
      alert("Wrong Username or Password");
    }
  }

  function Copyright(props) {
    return (
      <Typography variant="body2" color="text.secondary" align="center" {...props}>
        {'Copyright Â© '} 
          Matan Neyman {' '}
        {new Date().getFullYear()}
        {'.'}
      </Typography>
    );
  }



  const theme = createTheme();

  return (
    <div>
      {/* User Name: <input type="text" name='username' onChange={(e) => setUserLogin({ ...userLogin, username: e.target.value })}/> <br />

      Password: <input type="text" name='password' onChange={(e) => setUserLogin({ ...userLogin, password: e.target.value })}/> <br />

      <button onClick={checkUser}>Login</button> */}

<ThemeProvider theme={theme}>
      <Grid container component="main" sx={{ height: '100vh' }}>
        <CssBaseline />
        <Grid
          item
          xs={false}
          sm={4}
          md={7}
          sx={{
            backgroundImage: 'url(https://images.pexels.com/photos/2335048/pexels-photo-2335048.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2',
            backgroundRepeat: 'no-repeat',

            backgroundColor: (t) =>
              t.palette.mode === 'light' ? t.palette.grey[50] : t.palette.grey[900],
            backgroundSize: 'cover',
            backgroundPosition: 'center',

          }}
        />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <Box
            sx={{
              my: 8,
              mx: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <LockOutlinedIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Sign in
            </Typography>
            <Box component="form" noValidate  sx={{ mt: 1 }}>
              <TextField
                margin="normal"
                required
                fullWidth
                id="username"
                label="User Name"
                name="username"
                autoComplete="username"
                autoFocus
                onChange={(e) => setUserLogin({ ...userLogin, username: e.target.value })}
              />
              <TextField
                margin="normal"
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                id="password"
                autoComplete="current-password"
                onChange={(e) => setUserLogin({ ...userLogin, password: e.target.value })}
              />

              <Button
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2 }}
                onClick={checkUser}
              >
                Sign In
              </Button>

              <Copyright sx={{ mt: 5 }} />
            </Box>
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider>

    </div>
  )

  }

export default LoginComp
