import axios from 'axios'
import { useState, useEffect } from 'react'
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { doEditMovie } from '../redux/MoviesActions';

import Grid from "@mui/material/Grid";
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import TextField from "@mui/material/TextField";

const membersUrl = 'http://localhost:8000/members';

const EditMemberComp = () => {

  const dispatch = useDispatch()
  const navigate = useNavigate();
  const { memberid } = useParams();
  const members = useSelector(state => state.MembersReducer.members);
  const member = members.find((member)=> member._id === memberid)
  const [editMember, setEditMember] = useState(member)
  const [editMembersErrors, setEditMembersErrors] = useState({})
  const [isSubmit, setIsSubmit] = useState(false)

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmit(true);
    setEditMembersErrors(validate(editMember));

  };

  useEffect(() => {
    (async () => {if(Object.keys(setEditMembersErrors).length === 0 && isSubmit){
      // const { data } = await axios.post(`${membersUrl}`, editMember);
      // console.log(data)
      setEditMember(editMember);
      // dispatch(doAddMovie(movieToAdd));
      navigate('/mainpage')
    }})()
  }, [setEditMembersErrors])


  const cancel = () => {
    navigate('/mainpage')
}

  const validate = (values) => {
    const errors = {}
    const regex = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
    if(!values.name) {
        errors.name = "Name is required!";
    }
    if(!values.email) {
        errors.email = "Email is required!";
    }else if(!regex.test(values.email)){
      errors.email = "This is not a valid email format!"
    }
    if(!values.city) {
        errors.city = "City is required!";
    }
    return errors;
  }

  return (
    <div style={{marginTop: '20px', marginLeft: '10px'}}>
      <Card sx={{ width: '50%' }}>
      <form onSubmit={handleSubmit}>
      <CardContent>
        <Typography sx={{ fontSize: 17 }} color="text.secondary" gutterBottom>
            Edit Member: {editMember.name} 
        </Typography>   
        <Grid container direction={"column"} spacing={3}>
        <Grid item>
                  <TextField
                    id="outlined-textarea"
                    label="Name:"
                    value={editMember.name}
                    multiline
                    name="name"
                    onChange={(e) =>
                     setEditMember({ ...editMember, name: e.target.value })
                    }
                  />
                </Grid>
                <Grid item style={{color: 'red'}}>
                    {editMembersErrors.name}
                </Grid>
                <Grid item>
                  <TextField
                    id="outlined-textarea"
                    label="Email:"
                    value={editMember.email}
                    multiline
                    name="email"
                    onChange={(e) =>
                        setEditMember({ ...editMember, email: e.target.value })
                    }
                    />
                </Grid>
                <Grid item style={{color: 'red'}}>
                    {editMembersErrors.email}
                </Grid>
                <Grid item>
                  <TextField
                    id="outlined-textarea"
                    label="City:"
                    value={editMember.city}
                    multiline
                    name="city"
                    onChange={(e) =>
                    setEditMember({ ...editMember, image: e.target.value })
                    }
                    />
                </Grid>
                <Grid item style={{color: 'red'}}>
                    {editMembersErrors.city}
                </Grid>
                <Grid item>
                  <Button type="submit" variant="contained">
                    Save
                  </Button>{" "}
                  <Button variant="contained" onClick={cancel}>Cancel</Button> {' '}
                </Grid>
                </Grid>
      </CardContent>
      </form>
    </Card>
    </div>
  )
}

export default EditMemberComp
