import axios from 'axios'
import { useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { doEditMovie } from '../redux/MoviesActions';

import Grid from "@mui/material/Grid";
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import TextField from "@mui/material/TextField";


const moviesUrl = 'http://localhost:8000/movies';

const EditMovieComp = () => {
    const dispatch = useDispatch()
    const navigate = useNavigate();
    const { movieid } = useParams();
    const movies = useSelector(state => state.MoviesReducer.movies);
    const movie = movies.find((movie)=> movie._id === movieid)
    const [movieEdit, setMovieEdit] = useState(movie)

    const update = async () => {
        const { data } = await axios.put(`${moviesUrl}/${movie._id}`, movieEdit);
        console.log(data);
        dispatch(doEditMovie(movieEdit));
        navigate('/mainpage')
    }

    const cancel = () => {
        navigate('/mainpage')
    }

  return (
    <div style={{marginTop: '20px'}}>

<Card sx={{ width: '50%' }}>
      <CardContent>
        <Typography sx={{ fontSize: 17 }} color="text.secondary" gutterBottom>
            Edit Movie: {movie.name} 
        </Typography>   
        <Grid container direction={"column"} spacing={3}>
        <Grid item>
                  <TextField
                    id="outlined-textarea"
                    label="Name:"
                    value={movieEdit.name}
                    multiline
                    name="name"
                    onChange={(e) =>
                     setMovieEdit({ ...movieEdit, name: e.target.value })
                    }
                  />
                </Grid>
                <Grid item>
                  <TextField
                    id="outlined-textarea"
                    label="Genres:"
                    value={movieEdit.genres}
                    multiline
                    name="genres"
                    onChange={(e) =>
                        setMovieEdit({ ...movieEdit, genres: e.target.value.split(',') })
                    }
                    />
                </Grid>
                <Grid item>
                  <TextField
                    id="outlined-textarea"
                    label="Image URL:"
                    value={movieEdit.image}
                    multiline
                    name="image"
                    onChange={(e) =>
                    setMovieEdit({ ...movieEdit, image: e.target.value })
                    }
                    />
                </Grid>
                <Grid item>
                  <TextField
                    name="image"
                    label="Premired:"
                    type="date"
                    value={movieEdit.yearPremiered.split('T')[0]}
                    InputLabelProps={{ shrink: true, required: true }}
                    onChange={(e) =>
                    setMovieEdit({ ...movieEdit, yearPremiered : e.target.value+"T00:00:00.000Z" })
                    }
                    />
                </Grid>

                </Grid>
      </CardContent>
      <CardActions>
        <Button variant="contained" onClick={update}>update</Button> {' '}
        <Button variant="contained" onClick={cancel}>Cancel</Button> {' '}
      </CardActions>
    </Card>
    </div>
  )
}

export default EditMovieComp
