import axios from 'axios'
import { useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';

import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

const moviesUrl = 'http://localhost:8000/movies';

const EditMovieComp = () => {
    const [movieEdit, setMovieEdit] = useState({name: ''})
    const { movieid } = useParams();
    const movies = useSelector(state => state.MoviesReducer.movies);

    const movie = movies.find((movie)=> movie._id === movieid)

    const handleChange = (e) => {
        const { name, value } = e.target;
        setMovieEdit({...movieEdit, [name]: value})
    }

    const update = async () => {
        const resp = await axios.put(`${moviesUrl}/${movie.id}`, movieEdit);
        console.log(resp.data);
    }

  return (
    <div>
        Name: <input type="text" value={movie.name} name="name" onChange={(e) => setMovieEdit({ ...movieEdit, name: e.target.value })}/> <br />
        Genres: <input type="text" value={movie.genres} name="genres" onChange={handleChange}/> <br />
        Image URL: <input type="text" value={movie.image} name="image" onChange={handleChange}/> <br />
        Premired: <input type="text" value={movie.yearPremiered} name="premiered" onChange={handleChange}/> 

