import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios'
import { doLoadMovies } from '../redux/MoviesActions';


import MovieComp from './MovieComp'

const url = 'http://localhost:8000/movies';

const MoviesComp = () => {
    const dispatch = useDispatch()
    const [movies, setMovies] = useState([]);

    useEffect(() => {
      const fetchData = async () => {
        const { data: movies } = await axios.get(url);
        console.log(movies);
        setMovies(movies);
        dispatch(doLoadMovies(movies));
      }
      fetchData()
    }, [])

  return (
    <div>
        <h2>Movies</h2>
        <br />
        <button>All Movies</button> {' '}
        <button>Add Movie</button> {' '}
        Find Movie: <input type="text" /> {' '}
        <button>Find</button> <br /> <br />
        {movies.map((movie)=> {
            return <MovieComp key={movie._id} movie={movie}/>
        })}
    </div>
  )
}

export default MoviesComp
