import { useState } from 'react'
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';

const LoginComp = () => {
  const users = useSelector((state)=> state.users);
  const [userLogin, setUserLogin] = useState({ username: '', password: ''})
  const navigate = useNavigate();


  const checkUser = () => {
    users.forEach(user => {
      if(user.username === userLogin.username && user.password === userLogin.password){
        navigate(`/${user.username}/MainPage`)
      }
    });
  }


  return (
    <div>
      User Name: <input type="text" name='username' onChange={(event) => setUserLogin({ ...userLogin, username: event.target.value })}/> <br />
      Password: <input type="text" name='password' onChange={(event) => setUserLogin({ ...userLogin, password: event.target.value })}/> <br />

      <button onClick={checkUser}>Login</button>

    </div>
  )

  }

export default LoginComp
