import { useState } from "react";
// import Button from '@material-ui/core/Button'
// import ButtonGroup from '@material-ui/core/ButtonGroup'
import { ToggleButtonGroup, ToggleButton } from "@mui/material";
import MoviesComp from '../components/MoviesComp';
import SubscriptionsComp from '../components/SubscriptionsComp';
import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import axios from 'axios'
import { doLoadMovies } from "../redux/MoviesActions";
import { doLoadSubscriptions } from "../redux/SubscriptionsActions";
import { doLoadMembers } from "../redux/MembersActions";

const moviesUrl = 'http://localhost:8000/movies';
const subscriptionsUrl = 'http://localhost:8000/subscriptions';
const membersUrl = 'http://localhost:8000/members';

const MainPage = () => {
  const dispatch = useDispatch()
  const [currentAlignment, setCurrentAlignment] = useState("movies");

  useEffect(() => {
    const fetchMovies = async () => {
      const { data: movies } = await axios.get(moviesUrl);
      console.log(movies);
      dispatch(doLoadMovies(movies));
    }
    fetchMovies()
  }, [dispatch])

  useEffect(() => {
    const fetchSubscriptions = async () => {
      const { data: subscriptions } = await axios.get(subscriptionsUrl);
      console.log(subscriptions);
      dispatch(doLoadSubscriptions(subscriptions));
    }
    fetchSubscriptions()
  }, [dispatch])

  useEffect(() => {
    const fetchMembers = async () => {
      const { data: members } = await axios.get(membersUrl);
      console.log(members);
      dispatch(doLoadMembers(members));
    }
    fetchMembers()
  }, [])


  return (
    <div style={{padding: '10px'}}>
      <ToggleButtonGroup
        value={currentAlignment}
        onChange={(event, newAlignment) => {
          setCurrentAlignment(newAlignment);
        }} 
        exclusive
        variant="contained"
        aria-label="text formatting"
        color="primary"

      >
        <ToggleButton value="movies">Movies </ToggleButton>
        <ToggleButton value="subscriptions">Subscriptions</ToggleButton>
        <ToggleButton value="usersManagement">Users Management</ToggleButton>
        <ToggleButton value="logout">Logout</ToggleButton>

      </ToggleButtonGroup>
        <br />
        {/* {currentAlignment==='movies'? <MoviesComp/> : null}
        {currentAlignment==='subscriptions'? <SubscriptionsComp/> : null} */}
        {/* {currentAlignment==='movies'? <MoviesComp/> : null}
        {currentAlignment==='movies'? <MoviesComp/> : null} */}

    </div>
  );
};

export default MainPage;
