    <div>
        <h2>Subscriptions</h2>
        <br />

    <ToggleButtonGroup
      value={currentAlignment}
      onChange={(event, newAlignment) => {
        setCurrentAlignment(newAlignment);
      }} 
      exclusive
      variant="contained"
      aria-label="text formatting"
      color="primary"
      size='small'
    >
      <ToggleButton value="allmembers">All Members</ToggleButton>
      <ToggleButton value="addmember">Add Member</ToggleButton>
    </ToggleButtonGroup> {' '}
         <br /> <br />
        {currentAlignment==='allmembers' ? members.map((member)=> {
            return <MemberComp key={member._id} member={member}/> 
        }) : null}
        {currentAlignment==='addmember'? 
          <AddMemberComp/> : null
    }
    </div>
  )
}

export default MembersComp
