import axios from "axios";
import { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { doEditMovie } from "../redux/MoviesActions";

import Card from "@mui/material/Card";
import Grid from "@mui/material/Grid";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import TextField from "@mui/material/TextField";

const moviesUrl = "http://localhost:8000/movies";

const AddMovieComp = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const initialValues = { name: "", genres: "", image: "", premiered: Date };
  const [movieToAdd, setMovieToAdd] = useState({});

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (
      movieToAdd.name &&
      movieToAdd.genres &&
      movieToAdd.image &&
      movieToAdd.premiered
    ) {
      console.log(movieToAdd.toDate());
      const { data } = await axios.post(`${moviesUrl}`, movieToAdd);
      setMovieToAdd(movieToAdd);
      dispatch(doEditMovie(movieToAdd));
      console.log(data);
      console.log(movieToAdd);
    }
    // navigate(`/${username}/MainPage`)
  };

  return (
    <div style={{ marginBottom: '20px' }}>
      <Card sx={{ width: "75%" }}>
        <form onSubmit={handleSubmit}>
          <CardContent>
            <Typography variant="h7" component="div">
              <Grid container direction={"column"} spacing={5}>
                <Grid item>
                  <TextField
                    id="outlined-textarea"
                    label="Name:"
                    placeholder=""
                    multiline
                    name="name"
                    onChange={(e) =>
                      setMovieToAdd({ ...movieToAdd, name: e.target.value })
                    }
                  />
                </Grid>
                <Grid item>
                  <TextField
                    id="outlined-textarea"
                    label="Genres:"
                    placeholder=""
                    multiline
                    name="genres"
                    onChange={(e) =>
                      setMovieToAdd({
                        ...movieToAdd,
                        genres: e.target.value.split(","),
                      })
                    }
                  />
                </Grid>
                <Grid item>
                  <TextField
                    id="outlined-textarea"
                    label="Image URL:"
                    placeholder=""
                    multiline
                    name="iamge"
                    onChange={(e) =>
                      setMovieToAdd({ ...movieToAdd, image: e.target.value })
                    }
                  />
                </Grid>
                <Grid item>
                  <TextField
                    id="outlined-textarea"
                    label="Premired:"
                    placeholder=""
                    multiline
                    name="yearPremiered"
                    onChange={(e) =>
                      setMovieToAdd({
                        ...movieToAdd,
                        yearPremiered: e.target.value + "T00:00:00.000Z",
                      })
                    }
                  />
                </Grid>
                <Grid item>
                  <Button type="submit" variant="contained">
                    Save
                  </Button>{" "}
                  {/* <Button variant="contained" onClick={cancel}>Cancel</Button> {' '} */}
                </Grid>
              </Grid>
            </Typography>
          </CardContent>
        </form>
      </Card>
    </div>
  );
};

export default AddMovieComp;
