import { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import axios from 'axios'
import { doLoadMovies } from '../redux/MoviesActions';
import { ToggleButtonGroup, ToggleButton } from "@mui/material";


import MovieComp from './MovieComp'
import { padding } from '@mui/system';

const url = 'http://localhost:8000/movies';

const MoviesComp = () => {
    const dispatch = useDispatch()
    const [currentAlignment, setCurrentAlignment] = useState("allmovies");
    const [movies, setMovies] = useState([]);

    useEffect(() => {
      const fetchData = async () => {
        const { data: movies } = await axios.get(url);
        console.log(movies);
        setMovies(movies);
        dispatch(doLoadMovies(movies));
      }
      fetchData()
    }, [])

  return (
    <div style={{ border: '3px solid black', width: '500px', paddingLeft: '40px'}}>
        <h2>Movies</h2>
        <br />


    <ToggleButtonGroup
      value={currentAlignment}
      onChange={(event, newAlignment) => {
        setCurrentAlignment(newAlignment);
      }} 
      exclusive
      variant="contained"
      aria-label="text formatting"
      color="primary"
      size='small'
    >
      <ToggleButton value="allmovies">All Movies </ToggleButton>
      <ToggleButton value="addmovie">Add movie</ToggleButton>
    </ToggleButtonGroup> {' '}
        Find Movie: <input type="text" /> {' '}
        <button>Find</button> <br /> <br />
        {currentAlignment==='allmovies'? 
        movies.map((movie)=> {
            return <MovieComp key={movie._id} movie={movie}/> 
        }) : null
    }
    </div>
  )
}

export default MoviesComp
