const initialState = {
    users: [],
    movies: []
  };

  const applyUsersChange = (state = initialState, action) => {
    switch (action.type) {
      case 'LOAD':
        return { ...state, users: action.payload };

      default:
        return state;
    }
  };

  const applyMoviesChange = (state = initialState, action) => {
    switch (action.type) {
      case 'LOAD':
        return { ...state, movies: action.payload };

      default:
        return state;
    }
  };

 module.exports =  {applyUsersChange, applyMoviesChange}
