import axios from 'axios'
import { useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { doEditMovie } from '../redux/MoviesActions';

import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';

const moviesUrl = 'http://localhost:8000/movies';

const AddMovieComp = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const [movieToAdd, setMovieToAdd] = useState({})

    const save = async () => {
        const { data } = await axios.post(`${moviesUrl}`, movieToAdd);
        setMovieToAdd(movieToAdd)
        dispatch(doEditMovie(movieToAdd));
        console.log(data);
        console.log(movieToAdd);
        // navigate(`/${username}/MainPage`)
    }

  return (
    <div>
        <Card sx={{ width: '75%' }}>
      <CardContent>
        <Typography variant="h7" component="div">
                <TextField
          id="outlined-textarea"
          label="Name:"
          placeholder=""
          multiline
          name="name" onChange={(e) => setMovieToAdd({ ...movieToAdd, name: e.target.value })}
        /> 
        <TextField
          id="outlined-textarea"
          label="Genres:"
          placeholder=""
          multiline
          name="genres" onChange={(e) => setMovieToAdd({ ...movieToAdd, genres: e.target.value.split(',') })}
        />
        <TextField
          id="outlined-textarea"
          label="Image URL:"
          placeholder=""
          multiline
          name="iamge" onChange={(e) => setMovieToAdd({ ...movieToAdd, image: e.target.value })} 
        />
        <TextField
          id="outlined-textarea"
          label="Premired:"
          placeholder=""
          multiline
          name="premired" onChange={(e) => setMovieToAdd({ ...movieToAdd, premiered: e.target.value })}
        />
        </Typography>


      </CardContent>
      <CardActions>
        <Button variant="contained" onClick={save}>Save</Button> {' '}
        {/* <Button variant="contained" onClick={cancel}>Cancel</Button> {' '} */}
      </CardActions>
    </Card>
    </div>
  )
}

export default AddMovieComp
