import MovieComp from './MovieComp'
import AddMovieComp from './AddMovieComp';
import { useState } from 'react';
import { useSelector } from 'react-redux';

import { ToggleButtonGroup, ToggleButton } from "@mui/material";
import { useNavigate } from 'react-router-dom';

const MoviesComp = () => {
    const movies = useSelector(state => state.MoviesReducer.movies);
    const navigate = useNavigate();
    const [currentAlignment, setCurrentAlignment] = useState("allmovies");
    // const [movies, setMovies] = useState([]);

  return (
    <div style={{ border: '3px solid black', width: '500px', paddingLeft: '20px'}}>
        <h2>Movies</h2>
        <br />


    <ToggleButtonGroup
      value={currentAlignment}
      onChange={(event, newAlignment) => {
        setCurrentAlignment(newAlignment);
      }} 
      exclusive
      variant="contained"
      aria-label="text formatting"
      color="primary"
      size='small'
    >
      <ToggleButton value="allmovies">All Movies </ToggleButton>
      <ToggleButton value="addmovie">Add movie</ToggleButton>
    </ToggleButtonGroup> {' '}
        Find Movie: <input type="text" /> {' '}
        <button>Find</button> <br /> <br />
        {currentAlignment==='allmovies'? 
        movies.map((movie)=> {
            return <MovieComp key={movie._id} movie={movie}/> 
        }) : null}
        {currentAlignment==='addmovie'? 
           navigate('/mainpage/addmovie') : null
    }
    </div>
  )
}

export default MoviesComp
