const MainPage = () => {

    const [button, setButton] = useState({
        movies: true,
        subscriptions: false,
        usersManagement: false,
        logout: false
    })

    const handleButton = (e) => {
        const { name, value } = e.target;
        setButton({movies: false})
        setButton({subscriptions: false})
        setButton({usersManagement: false})
        setButton({logout: false})
        setButton({...button, [name]: ![name].value})
        // setButton({button: false})
        console.log(button)
    }

  return (
    <div>
        {/* <button onClick={(e)=> e.target.style.background = 'yellow'}>Movies</button> */}
        <button name='movies' onClick={ handleButton }>Movies</button>

    </div>
  )
}

