import axios from 'axios';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

import SubWatchedComp from './SubWatchedComp';
import { useNavigate } from 'react-router-dom';


const moviesUrl = 'http://localhost:8000/movies';

const MovieComp = ({movie}) => {

    const navigate = useNavigate();
    const editMovie = () => {
        navigate(`/editmovie/${movie._id}`)
    }

    const deleteMovie = async () => {
            const {data} = await axios.delete(`${moviesUrl}/${movie._id}`);
            console.log(data);
            navigate('/mainpage')
    }


  return (
    <div style={{paddingBottom: '20px'}} >

        <Card style={{boxShadow: " rgba(0, 0, 0, 0.4) 0px 2px 4px, rgba(0, 0, 0, 0.3) 0px 7px 13px -3px, rgba(0, 0, 0, 0.2) 0px -3px 0px inset"}} sx={{ maxWidth: 345 }}>
      <CardMedia
        component="img"
        alt="green iguana"
        height="auto"
        image={movie.image}
      />
      <CardContent >
        <Typography gutterBottom variant="h6" component="div">
        {movie.name}
        </Typography>
        <Typography variant="body1" color="text.secondary">
        Genres: {movie.genres.map(name => `"${name}", `)}
        </Typography>
        <SubWatchedComp id={movie._id}/>
      </CardContent>

      <CardActions>
        <Button onClick={editMovie} size="medium">Edit</Button>
        <Button onClick={deleteMovie} size="medium">Delete</Button>
      </CardActions>

    </Card>

    <br />
    </div>
  )
}

export default MovieComp
