import {useState ,useEffect} from 'react'
import { Routes, Route } from 'react-router-dom';
import LoginPage from './pages/LoginPage'
import MainPage from './pages/MainPage'
import AddMovieComp from './components/AddMovieComp'
import EditMovieComp from './components/EditMovieComp'
import EditMemberComp from './components/EditMemberComp'
import AddMemberComp from './components/AddMemberComp';
import MembersComp from './components/MembersComp'
import MoviesComp from './components/MoviesComp'
import UsersManagement from './components/UsersManagement';

import { useNavigate } from 'react-router-dom';
import { ToggleButtonGroup, ToggleButton } from "@mui/material";

import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';



const App = () => {
  const navigate = useNavigate();
  const [currentAlignment, setCurrentAlignment] = useState("movies");
  let data = sessionStorage.getItem('key');

  const logout = () => {
    navigate('/')
    sessionStorage.removeItem('key')
  }
  useEffect(() => {

  }, [currentAlignment])


  return (
    <div>
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
          Movies - Subscriptions Web Site
          </Typography>
          <Typography variant="h5" component="div" sx={{ flexGrow: 0.05 }}>
          {data} 
          </Typography>
        <Button variant="outlined" color="inherit" onClick={logout}>LOGOUT</Button>
        </Toolbar>
      </AppBar>
    </Box>

    <ToggleButtonGroup
        value={currentAlignment}
        onChange={(event, newAlignment) => {
          setCurrentAlignment(newAlignment);
        }} 
        exclusive
        variant="contained"
        aria-label="text formatting"
        color="primary"
      >
        <ToggleButton value="movies" onClick={()=> navigate('/mainpage/movies')}>Movies</ToggleButton>
        <ToggleButton value="subscriptions" onClick={()=> navigate('/mainpage/subs')}>Subscriptions</ToggleButton>
        <ToggleButton value="usersmanagement" onClick={()=> navigate('/mainpage/users')}>Users Management</ToggleButton>

      </ToggleButtonGroup>

      <Routes>
        <Route path='/' element={<LoginPage />} />
        <Route path='/mainpage/' element={<MainPage />} />
        <Route path='/mainpage/:sub' element={<MainPage />} />
        <Route path='/movies/addmovie/' element={<AddMovieComp />} />
        <Route path='/members/editmember/:memberid' element={<EditMemberComp />} />
        <Route path='/mainpage/addmember/' element={<AddMemberComp />} />
        <Route path='/movies/editmovie/:movieid' element={<EditMovieComp/>}/>
        <Route path='/mainpage/subs' element={<MembersComp/>}/>
        <Route path='/mainpage/movies' element={<MoviesComp/>}/>
        <Route path='/mainpage/users' element={<UsersManagement/>}/>

      </Routes>
    </div>
  )
}

export default App
