import { useState } from 'react'

const MainPage = () => {

    const [button, setButton] = useState({
        movies: true,
        subscriptions: false,
        usersManagement: false,
        logout: false
    })

    const handleButton = (e) => {
        const { name } = e.target
        setButton({movies: false})
        setButton({subscriptions: false})
        setButton({usersManagement: false})
        setButton({logout: false})
        setButton({...button, [name]: true})
        e.target.style.background = 'yellow'


        console.log(button.movies)
    }

  return (
    <div>
        {/* <button onClick={(e)=> e.target.style.background = 'yellow'}>Movies</button> */}
        <button style={button.movies ? {background: 'yellow'}: {background: 'none'}} name='movies' onClick={ handleButton }>Movies</button> {' '}
        <button name='subscriptions' onClick={ handleButton }>Subscriptions</button> {' '}
        <button name='usersManagement' onClick={ handleButton }>Users Management</button> {' '}
        <button name='logout' onClick={ handleButton }>Logout</button> {' '}

    </div>
  )
}

export default MainPage
