import { useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';

const EditMovieComp = () => {
    const [movieEdit, setMovieEdit] = useState({})
    const { movieid } = useParams();
    const movies = useSelector(state => state.MoviesReducer.movies);

    const movie = movies.find((movie)=> movie._id === movieid)

    const handleChange = (e) => {
        const { name, value } = e.target;
        setMovieEdit({...movieEdit, [name]: value})
    }

  return (
    <div>
        <h3>Edit Movie: {movie.name}</h3>
        Name: <input type="text" value={movie.name} name="name" onChange={handleChange}/> <br />
        Genres: <input type="text" value={movie.genres} name="genres" onChange={handleChange}/> <br />
        Image URL: <input type="text" value={movie.image} name="image" onChange={handleChange}/> <br />
        Premired: <input type="text" value={movie.yearPremiered} name="premiered" onChange={handleChange}/> <br />
    </div>
  )
}

export default EditMovieComp
