import axios from "axios";
import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { doAddMovie } from "../redux/MoviesActions";
import MoviesComp from './MoviesComp';

import Card from "@mui/material/Card";
import Grid from "@mui/material/Grid";
import CardActions from '@mui/material/CardActions';
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import TextField from "@mui/material/TextField";

const moviesUrl = "http://localhost:8000/movies";

const AddMovieComp = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [movieToAdd, setMovieToAdd] = useState({});
  const [movieToAddErrors, setMovieToAddErrors] = useState({})
  const [isSubmit, setIsSubmit] = useState(false)

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(movieToAdd)
    setMovieToAddErrors(validate(movieToAdd));
    setIsSubmit(true);
    debugger
    if(Object.keys(movieToAddErrors).length === 0 && isSubmit){
      const { data } = await axios.post(`${moviesUrl}`, movieToAdd);
      console.log(data)
      setMovieToAdd(movieToAdd);
      dispatch(doAddMovie(movieToAdd));
      navigate('/mainpage')
    }
  };

  const cancel = () => {
    navigate('/mainpage')
}

  const validate = (values) => {
    const errors = {}
    if(!values.name) {
        errors.name = "Name is required!";
    }
    if(!values.genres) {
        errors.genres = "Genres is required!";
    }
    if(!values.image) {
        errors.image = "Image is required!";
    }
    if(!values.yearPremiered) {
        errors.yearPremiered = "Premiered is required!";
    }
    return errors;
  }

  return (
    <div style={{ marginTop: '20px' }}>
      <Card sx={{ width: "50%" }}>
        <form onSubmit={handleSubmit}>
          <CardContent>
            <Typography variant="h7" component="div">
              <Grid container direction={"column"} spacing={2}>
                <Grid item>
                  <TextField
                    id="outlined-textarea"
                    label="Name:"
                    multiline
                    name="name"
                    onChange={(e) =>
                      setMovieToAdd({ ...movieToAdd, name: e.target.value })
                    }
                    />
                </Grid>
                <Grid item>
                    {movieToAddErrors.name}
                </Grid>
                <Grid item>
                  <TextField
                    id="outlined-textarea"
                    label="Genres:"
                    multiline
                    name="genres"
                    onChange={(e) =>
                      setMovieToAdd({
                        ...movieToAdd,
                        genres: e.target.value.split(","),
                      })
                    }
                  />
                </Grid>
                <Grid item>
                    {movieToAddErrors.genres}
                </Grid>
                <Grid item>
                  <TextField
                    id="outlined-textarea"
                    label="Image URL:"
                    multiline
                    name="image"
                    onChange={(e) =>
                      setMovieToAdd({ ...movieToAdd, image: e.target.value })
                    }
                  />
                </Grid>
                <Grid item>
                    {movieToAddErrors.image}
                </Grid>
                <Grid item>
                  <TextField
                    id="outlined-textarea"
                    type='date'
                    name="yearPremiered"
                    onChange={(e) =>
                      setMovieToAdd({
                        ...movieToAdd,
                        yearPremiered: e.target.value + "T00:00:00.000Z",
                      })
                    }
                  />
                </Grid>
                <Grid item>
                    {movieToAddErrors.yearPremiered}
                </Grid>
                <Grid item>
                  <Button type="submit" variant="contained">
                    Save
                  </Button>{" "}
                  <Button variant="contained" onClick={cancel}>Cancel</Button> {' '}
                </Grid>
              </Grid>
            </Typography>
          </CardContent>
        </form>
      </Card>
    </div>
  );
};

export default AddMovieComp;
