import {useState} from 'react'
import MemberMovieWatched from './MemberMovieWatched'
import Card from '@mui/material/Card';
import Typography from '@mui/material/Typography';
import {  useNavigate } from 'react-router-dom';
import EditMemberComp from './EditMemberComp'


const MemberComp = ({member}) => {
    const navigate = useNavigate();
    const [toggle, setToggle] = useState(false);
    const toggleChecked = () => setToggle(toggle => !toggle);

  return (

        <div style={{paddingBottom: '20px'}} >

      <Card style={{boxShadow: " rgba(0, 0, 0, 0.4) 0px 2px 4px, rgba(0, 0, 0, 0.3) 0px 7px 13px -3px, rgba(0, 0, 0, 0.2) 0px -3px 0px inset"}} sx={{ maxWidth: 345 }}>

      <Typography gutterBottom variant="h6" component="div">
      Name: {member.name} <br />
      Email: {member.email} <br />
      City: {member.city} <br />
      {/* <button onClick={()=> navigate(`/members/editmember/${member._id}`)}>Edit</button> */}
      <button type="button" onClick={toggleChecked}>Edit</button>
    {toggle && <EditMemberComp member={member}/> }
    {!toggle && null}
      </Typography>
      <MemberMovieWatched id={member._id}/>




  </Card>

    </div>
  )
}

export default MemberComp
